@inline function _dot_xHy_gpu(x::AbstractVector{T},H::AbstractArray{T},y::AbstractVector{T}) where {T} 
    val = zero(T)
    @inbounds for j in 1:3
        val += x[j]*(H[j,1]*y[1]+H[j,2]*y[2]+H[j,3]*y[3])
    end
    
    return val
end 

@inline function _normHF(H::AbstractArray{T}) where {T} 
    val = zero(T)
    @inbounds for j in 1:9
        val += H[j]*H[j]
    end
    
    return sqrt(val)
end 

@inline function _normalize!(x::AbstractVector{T}) where {T} 
    val = zero(T)
    @inbounds for j in 1:3
        val += x[j]*x[j]
    end
    normx = sqrt(val)
    @inbounds for j in 1:3
        x[j] /= normx
    end     
    return nothing
end 